insert into event (event_id, description, time) values (1, 'Он  бродил  по  поверхности  планет Семи  Солнц  --  первый  человек за
    миллиард  лет.  Но  теперь  это  для   него  мало  что  значило.  Порой  ему
    представлялось,  что он  отдал  бы  все свои  достижения, если бы только мог
    услышать крик новорожденного и знать, что это дитя -- его собственное...', '2023-09-29');
insert into event (event_id, description, time) values (2, 'Олвин оглянулся — убедиться, что никто из его товарищей не отстал. Алистра шагала вплотную за ним, держа шар холодного, немеркнущего огня, который с самого начала их приключений в недрах Хрустальной горы вырывал из тьмы то немыслимые ужасы, то неподражаемую красоту. За Алистрой, покряктывая под тяжестью видеопроектора, двигались Нарилльян и Флоранус. Олвин мимолетно подивился, почему это проекторы сделаны такими тяжелыми, — ведь снабдить их гравитационными нейтрализаторами совсем несложно. Он постоянно задумывался и о таких вот вещах — даже в разгар самых отчаянных приключений. И когда его посещали подобные мысли, ему чудилось, что ткань действительности испытывает какой-то мгновенный трепет, и за пределами мира сиюминутных ощущений он схватывал проблеск иной, совсем не похожей на эту, действительности…', '2023-09-01');
